INSERT INTO `events` (`id`, `id_dci`, `name`, `start_day`, `end_day`, `place`, `time`, `has_cost`, `directed_to`, `link`, `description`, `dci_status`, `created_at`, `updated_at`, `deleted_at`, `user_id`) VALUES (33, '150126', 'Seminario de Orientación BUAP, modalidad virtual ', '2015-02-14', '2015-04-17', 'Dirección General de Innovación Educativa', '17:00:00', 1, 'Público en general', 'http://seminario.buap.mx/seminario/virtual/', 'Seminario de orientación BUAP 2015\r\nModalidad Virtual\r\n¡Prepárate en línea para el examen de admisión a la BUAP desde donde estés!\r\nRegistro abierto hasta el 15 de abril en seminario.buap.mx\r\nDuración del 14 de febrero al 17 de abril\r\nEsta es la opción de preparación para aquellos interesados en ingresar a la BUAP que viven fuera del Estado de Puebla o el extranjero porque les permite potenciar sus habilidades de razonamiento a través de un curso en línea con la misma calidad y respaldo del Seminario de Orientación presencial ¿Cómo? Fortaleciendo sus habilidades para presentar con éxito la Prueba de Aptitud Académica (PAA). \r\nEste seminario cuenta con sesiones online conformadas por las áreas de Razonamiento Verbal, Razonamiento Matemático y Redacción Indirecta donde el aspirante descubre sus áreas de oportunidad, evalúa sus avances, confirma sus respuestas, conoce el examen y estudia de manera independiente y autónoma: a su ritmo, desde su lugar de residencia, sin gastos adicionales en transporte, materiales y alimentación, siempre con el apoyo y acompañamiento de Asesores Especialistas que aclararán sus dudas, a través  de chats y foros virtuales.\r\nInformes:\r\nTeléfono 2 29 55 00 extensión 7902.\r\nInversión:\r\n $500.00 \r\nEstudiantes a graduarse en  julio de 2015 de las Preparatorias de la BUAP podrán cursarlo sin costo.\r\n', 'Pendiente', '2015-01-26 20:28:52', '2015-01-26 20:30:22', NULL, 16);

